mapper.select:
select

from t_project
where status = 2

    and type = #{type,jdbcType=INTEGER}


    and area =#{area,jdbcType=INTEGER}


    and financing_limit =#{financingLimit,jdbcType=INTEGER}


    and industry =#{industry,jdbcType=INTEGER}


    and title like CONCAT('%','${keyword}','%' )

ORDER BY create_time DESC
limit #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select
id
from t_project
where 1=1

    and type = #{type,jdbcType=INTEGER}


    and area =#{area,jdbcType=INTEGER}


    and financing_limit =#{financingLimit,jdbcType=INTEGER}


    and industry =#{industry,jdbcType=INTEGER}


    and title like CONCAT('%','${keyword}','%' )

select
tb_a.id, tb_a.title,tb_a.demand,tb_a.logo
FROM t_project tb_a INNER JOIN t_project_suggest tb_b ON tb_a.id = tb_b.projectid
where
tb_b.investorid = #{investorid,jdbcType=INTEGER}

    and tb_b.status =#{status,jdbcType=INTEGER}

ORDER by tb_b.create_time DESC
limit #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select
count(tb_a.id)
FROM t_project tb_a INNER JOIN t_project_suggest tb_b ON tb_a.id = tb_b.projectid
where
tb_b.investorid = #{investorid,jdbcType=INTEGER}

    and tb_b.status =#{status,jdbcType=INTEGER}

select

from t_project
where status = #{status,jdbcType=INTEGER}
order by create_time desc
limit #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(id)
from t_project
where status = #{status,jdbcType=INTEGER}
